import React, { useState, useEffect } from 'react';
import './App.css';
import { Bar, Pie } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const API_BASE_URL = 'http://localhost:8000/api';

function App() {
  const [datasets, setDatasets] = useState([]);
  const [currentDataset, setCurrentDataset] = useState(null);
  const [selectedDatasetId, setSelectedDatasetId] = useState('');
  const [currentPage, setCurrentPage] = useState(0);
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    loadDatasets();
  }, []);

  const loadDatasets = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/datasets/`);
      const data = await response.json();

      let datasetList = [];
      if (data.results) {
        datasetList = data.results;
      } else if (Array.isArray(data)) {
        datasetList = data;
      }

      setDatasets(datasetList);
    } catch (error) {
      console.error('Error loading datasets:', error);
      alert('Failed to load datasets. Make sure backend is running.');
    }
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    if (!file.name.endsWith('.csv')) {
      alert('Please select a CSV file');
      return;
    }

    setUploading(true);

    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await fetch(`${API_BASE_URL}/datasets/upload/`, {
        method: 'POST',
        body: formData,
      });

      if (response.ok) {
        const data = await response.json();
        alert('‚úÖ File uploaded successfully!\n\nSwitch to "Analyze Report" to view the data.');
        await loadDatasets();
        setCurrentDataset(data);
        setCurrentPage(1); // Auto-switch to analysis page
      } else {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Upload failed');
      }
    } catch (error) {
      alert(`‚ùå Upload failed: ${error.message}`);
    } finally {
      setUploading(false);
      event.target.value = ''; // Reset file input
    }
  };

  const handleDatasetSelect = async (event) => {
    const datasetId = event.target.value;
    setSelectedDatasetId(datasetId);

    if (!datasetId) {
      setCurrentDataset(null);
      return;
    }

    try {
      const response = await fetch(`${API_BASE_URL}/datasets/${datasetId}/`);

      if (response.status === 404) {
        alert('‚ö†Ô∏è Dataset not found. It may have been deleted.\n\nRefreshing dataset list...');
        setCurrentDataset(null);
        await loadDatasets();
        return;
      }

      if (response.ok) {
        const data = await response.json();
        setCurrentDataset(data);
        setCurrentPage(1); // Auto-switch to analysis page
      } else {
        throw new Error('Failed to load dataset');
      }
    } catch (error) {
      alert(`‚ùå Error loading dataset: ${error.message}`);
      setCurrentDataset(null);
    }
  };

  const handleDownloadPDF = async () => {
    if (!currentDataset) {
      alert('‚ö†Ô∏è Please select a dataset first before downloading PDF\n\nGo to "Upload Dataset" page to select a dataset.');
      return;
    }

    const datasetId = currentDataset.id;

    try {
      const response = await fetch(`${API_BASE_URL}/datasets/${datasetId}/download_pdf/`);

      if (response.status === 404) {
        alert('‚ö†Ô∏è Dataset not found or has been deleted.\n\nRefreshing dataset list...');
        setCurrentDataset(null);
        await loadDatasets();
        return;
      }

      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `equipment_report_${datasetId}.pdf`;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);

        alert(`‚úÖ PDF report downloaded successfully!`);
      } else {
        throw new Error('Failed to download PDF');
      }
    } catch (error) {
      alert(`‚ùå Download failed: ${error.message}`);
    }
  };

  const switchPage = (pageIndex) => {
    setCurrentPage(pageIndex);
  };

  // Chart data
  const getBarChartData = () => {
    if (!currentDataset || !currentDataset.summary) return null;

    const summary = currentDataset.summary;
    return {
      labels: ['Flowrate', 'Pressure', 'Temperature'],
      datasets: [
        {
          label: 'Average Values',
          data: [
            summary.avg_flowrate || 0,
            summary.avg_pressure || 0,
            summary.avg_temperature || 0,
          ],
          backgroundColor: ['#36A2EB', '#FF6384', '#FFCE56'],
        },
      ],
    };
  };

  const getPieChartData = () => {
    if (!currentDataset || !currentDataset.summary || !currentDataset.summary.equipment_types) {
      return null;
    }

    const types = currentDataset.summary.equipment_types;
    return {
      labels: Object.keys(types),
      datasets: [
        {
          data: Object.values(types),
          backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF', '#FF9F40'],
        },
      ],
    };
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: true,
    plugins: {
      legend: {
        position: 'top',
      },
    },
  };

  return (
    <div className="app">
      {/* Sidebar */}
      <div className="sidebar">
        <div className="logo">üß™ Equipment<br />Visualizer</div>

        <button
          className={`nav-button ${currentPage === 0 ? 'active' : ''}`}
          onClick={() => switchPage(0)}
        >
          üì§ Upload Dataset
        </button>

        <button
          className={`nav-button ${currentPage === 1 ? 'active' : ''}`}
          onClick={() => switchPage(1)}
        >
          üìä Analyze Report
        </button>

        <button
          className={`nav-button ${currentPage === 2 ? 'active' : ''}`}
          onClick={() => switchPage(2)}
        >
          üìã Equipment Report
        </button>

        <div className="footer">Version 1.0<br />¬© 2026</div>
      </div>

      {/* Content Area */}
      <div className="content">
        {/* Page 1: Upload & Select */}
        {currentPage === 0 && (
          <div className="page">
            <h1 className="page-title">Upload & Select Dataset</h1>

            <div className="card">
              <h2 className="card-title">üìÅ Upload New Dataset</h2>
              <p className="card-description">
                Select a CSV file containing equipment data to upload and analyze.
              </p>
              <label className="upload-button">
                {uploading ? '‚è≥ Uploading...' : 'üì§ Choose CSV File'}
                <input
                  type="file"
                  accept=".csv"
                  onChange={handleFileUpload}
                  disabled={uploading}
                  style={{ display: 'none' }}
                />
              </label>
            </div>

            <div className="card">
              <h2 className="card-title">üìä Select Existing Dataset</h2>
              <p className="card-description">
                Choose from previously uploaded datasets to view and analyze.
              </p>
              <div className="select-container">
                <label className="select-label">Dataset:</label>
                <select
                  className="dataset-select"
                  value={selectedDatasetId}
                  onChange={handleDatasetSelect}
                >
                  <option value="">
                    {datasets.length === 0 ? 'No datasets available' : 'Select a dataset...'}
                  </option>
                  {datasets.map((ds) => (
                    <option key={ds.id} value={ds.id}>
                      {ds.filename} - {ds.upload_date.substring(0, 10)}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          </div>
        )}

        {/* Page 2: Analysis */}
        {currentPage === 1 && (
          <div className="page">
            <h1 className="page-title">Data Analysis & Visualization</h1>

            {currentDataset && currentDataset.summary ? (
              <>
                {/* Stats Cards */}
                <div className="stats-grid">
                  <div className="stat-card stat-card-blue">
                    <div className="stat-title">Total Records</div>
                    <div className="stat-value">{currentDataset.row_count || 0}</div>
                  </div>
                  <div className="stat-card stat-card-purple">
                    <div className="stat-title">Avg Flowrate</div>
                    <div className="stat-value">
                      {currentDataset.summary.avg_flowrate?.toFixed(1) || 0}
                    </div>
                  </div>
                  <div className="stat-card stat-card-red">
                    <div className="stat-title">Avg Pressure</div>
                    <div className="stat-value">
                      {currentDataset.summary.avg_pressure?.toFixed(1) || 0}
                    </div>
                  </div>
                  <div className="stat-card stat-card-orange">
                    <div className="stat-title">Avg Temperature</div>
                    <div className="stat-value">
                      {currentDataset.summary.avg_temperature?.toFixed(1) || 0}
                    </div>
                  </div>
                </div>

                {/* Charts */}
                <div className="card">
                  <h2 className="card-title">üìà Visual Analytics</h2>
                  <div className="charts-container">
                    <div className="chart-wrapper">
                      {getBarChartData() && (
                        <Bar data={getBarChartData()} options={chartOptions} />
                      )}
                    </div>
                    <div className="chart-wrapper">
                      {getPieChartData() && (
                        <Pie data={getPieChartData()} options={chartOptions} />
                      )}
                    </div>
                  </div>
                </div>
              </>
            ) : (
              <div className="card">
                <p className="empty-state">
                  üìä No dataset selected. Please upload or select a dataset from the "Upload
                  Dataset" page.
                </p>
              </div>
            )}
          </div>
        )}

        {/* Page 3: Equipment Records */}
        {currentPage === 2 && (
          <div className="page">
            <div className="page-header">
              <h1 className="page-title">Equipment Records</h1>
              <button className="download-button" onClick={handleDownloadPDF}>
                üìÑ Download PDF Report
              </button>
            </div>

            <div className="card">
              <h2 className="card-title">üìã Detailed Equipment Data</h2>

              {currentDataset && currentDataset.records ? (
                <div className="table-container">
                  <table className="data-table">
                    <thead>
                      <tr>
                        <th>Equipment Name</th>
                        <th>Type</th>
                        <th>Flowrate</th>
                        <th>Pressure</th>
                        <th>Temperature</th>
                      </tr>
                    </thead>
                    <tbody>
                      {currentDataset.records.map((record, index) => (
                        <tr key={index}>
                          <td>{record.equipment_name}</td>
                          <td>{record.equipment_type}</td>
                          <td>{record.flowrate}</td>
                          <td>{record.pressure}</td>
                          <td>{record.temperature}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              ) : (
                <p className="empty-state">
                  üìã No dataset selected. Please upload or select a dataset from the "Upload
                  Dataset" page.
                </p>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
